     1→import { Hono } from 'hono';
     2→import { cors } from 'hono/cors';
     3→import { getModelForKey } from './validator.js';
     4→import { proxyRequest } from './proxy.js';
     5→import { proxyAnthropicRequest } from './anthropic.js';
     6→import { checkRateLimit } from './ratelimit.js';
     7→import { authMiddleware, getApiKeyFromContext, type AuthContext } from './middleware/auth.js';
     8→import { rateLimitMiddleware } from './middleware/rateLimit.js';
     9→import { createProxyHandler } from './handlers/proxyHandler.js';
    10→import type { StatsResponse } from './types.js';
    11→
    12→type Bindings = {
    13→  ZAI_API_KEY: string;
    14→  DEFAULT_MODEL: string;
    15→  PORT: string;
    16→};
    17→
    18→const app = new Hono<{ Bindings: Bindings; Variables: AuthContext }>();
    19→
    20→// Enable CORS
    21→app.use('/*', cors({
    22→  origin: '*',
    23→  allowMethods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
    24→  allowHeaders: ['Content-Type', 'Authorization', 'x-api-key'],
    25→}));
    26→
    27→// Stats endpoint
    28→app.get('/stats', authMiddleware, async (c) => {
    29→  const apiKey = getApiKeyFromContext(c as any);
    30→
    31→  // Get rate limit info
    32→  const rateLimit = checkRateLimit(apiKey);
    33→
    34→  // Calculate model
    35→  const model = getModelForKey(apiKey);
    36→
    37→  const stats: StatsResponse = {
    38→    key: apiKey.key,
    39→    name: apiKey.name,
    40→    model,
    41→    token_limit_per_5h: apiKey.token_limit_per_5h,
    42→    expiry_date: apiKey.expiry_date,
    43→    created_at: apiKey.created_at,
    44→    last_used: apiKey.last_used,
    45→    is_expired: new Date(apiKey.expiry_date) < new Date(),
    46→    current_usage: {
    47→      tokens_used_in_current_window: rateLimit.tokensUsed,
    48→      window_started_at: rateLimit.windowStart,
    49→      window_ends_at: rateLimit.windowEnd,
    50→      remaining_tokens: Math.max(0, rateLimit.tokensLimit - rateLimit.tokensUsed),
    51→    },
    52→    total_lifetime_tokens: apiKey.total_lifetime_tokens,
    53→  };
    54→
    55→  return c.json(stats);
    56→});
    57→
    58→// Create proxy handlers
    59→const openaiProxyHandler = createProxyHandler(proxyRequest);
    60→const anthropicProxyHandler = createProxyHandler(proxyAnthropicRequest);
    61→
    62→// Anthropic Messages API - must be defined before /v1/* catch-all
    63→app.post('/v1/messages', authMiddleware, rateLimitMiddleware, anthropicProxyHandler);
    64→
    65→// OpenAI-Compatible API - catch-all for /v1/*
    66→app.all('/v1/*', authMiddleware, rateLimitMiddleware, openaiProxyHandler);
    67→
    68→// Health check
    69→app.get('/health', (c) => {
    70→  return c.json({ status: 'ok', timestamp: new Date().toISOString() });
    71→});
    72→
    73→// Root
    74→app.get('/', (c) => {
    75→  return c.json({
    76→    name: 'Proxy Gateway',
    77→    version: '1.0.0',
    78→    endpoints: {
    79→      health: 'GET /health',
    80→      stats: 'GET /stats',
    81→      openai_compatible: 'ALL /v1/* (except /v1/messages)',
    82→      anthropic_compatible: 'POST /v1/messages',
    83→    },
    84→  });
    85→});
    86→
    87→const port = parseInt(process.env.PORT || '3000');
    88→
    89→export default {
    90→  port,
    91→  fetch: app.fetch,
    92→};
    93→
    94→console.log(`Proxy Gateway starting on port ${port}`);
    95→

