     1→/**
     2→ * Integration Test Helpers
     3→ *
     4→ * Provides utilities for setting up test servers, making HTTP requests,
     5→ * and validating responses in integration tests.
     6→ */
     7→
     8→import { serve } from 'bun';
     9→import app from '../../src/index';
    10→import fs from 'fs';
    11→import path from 'path';
    12→import type { ApiKey, UsageWindow } from '../../src/types';
    13→
    14→/**
    15→ * Test server interface
    16→ */
    17→export interface TestServer {
    18→  url: string;
    19→  port: number;
    20→  stop: () => Promise<void>;
    21→}
    22→
    23→/**
    24→ * HTTP response wrapper
    25→ */
    26→export interface TestResponse {
    27→  status: number;
    28→  headers: Headers;
    29→  body: string;
    30→  json: () => any;
    31→}
    32→
    33→/**
    34→ * Rate limit info for validation
    35→ */
    36→export interface RateLimitInfo {
    37→  allowed: boolean;
    38→  tokensUsed: number;
    39→  tokensLimit: number;
    40→  windowStart?: string;
    41→  windowEnd?: string;
    42→  reason?: string;
    43→  retryAfter?: number;
    44→}
    45→
    46→/**
    47→ * Starts a test server instance
    48→ *
    49→ * @param port - Optional port number (defaults to random available port)
    50→ * @returns Test server instance with URL and stop function
    51→ */
    52→export async function startTestServer(port?: number): Promise<TestServer> {
    53→  // Find available port if not specified
    54→  const actualPort = port || (await findAvailablePort());
    55→
    56→  // Start the server
    57→  const server = serve({
    58→    fetch: app.fetch,
    59→    port: actualPort,
    60→  });
    61→
    62→  return {
    63→    url: `http://localhost:${actualPort}`,
    64→    port: actualPort,
    65→    stop: async () => {
    66→      server.stop();
    67→    },
    68→  };
    69→}
    70→
    71→/**
    72→ * Finds an available port for testing
    73→ */
    74→async function findAvailablePort(): Promise<number> {
    75→  // Try ports starting from 3001
    76→  for (let port = 3001; port < 4000; port++) {
    77→    try {
    78→      const server = serve({ port, fetch: () => new Response('ok') });
    79→      server.stop();
    80→      return port;
    81→    } catch (e) {
    82→      // Port in use, try next
    83→      continue;
    84→    }
    85→  }
    86→  throw new Error('No available port found for test server');
    87→}
    88→
    89→/**
    90→ * Makes an HTTP request to the test server
    91→ *
    92→ * @param url - Request URL
    93→ * @param options - Request options
    94→ * @returns Test response wrapper
    95→ */
    96→export async function makeRequest(
    97→  url: string,
    98→  options: RequestInit = {}
    99→): Promise<TestResponse> {
   100→  const response = await fetch(url, options);
   101→
   102→  const body = await response.text();
   103→
   104→  return {
   105→    status: response.status,
   106→    headers: response.headers,
   107→    body,
   108→    json: () => JSON.parse(body),
   109→  };
   110→}
   111→
   112→/**
   113→ * Makes an authenticated request with API key
   114→ *
   115→ * @param url - Request URL
   116→ * @param apiKey - API key for authentication
   117→ * @param options - Request options
   118→ * @returns Test response wrapper
   119→ */
   120→export async function makeAuthenticatedRequest(
   121→  url: string,
   122→  apiKey: string,
   123→  options: RequestInit = {}
   124→): Promise<TestResponse> {
   125→  const headers = {
   126→    ...options.headers,
   127→    Authorization: `Bearer ${apiKey}`,
   128→  };
   129→
   130→  return makeRequest(url, { ...options, headers });
   131→}
   132→
   133→/**
   134→ * Makes an authenticated request with x-api-key header
   135→ *
   136→ * @param url - Request URL
   137→ * @param apiKey - API key for authentication
   138→ * @param options - Request options
   139→ * @returns Test response wrapper
   140→ */
   141→export async function makeRequestWithXApiKey(
   142→  url: string,
   143→  apiKey: string,
   144→  options: RequestInit = {}
   145→): Promise<TestResponse> {
   146→  const headers = {
   147→    ...options.headers,
   148→    'x-api-key': apiKey,
   149→  };
   150→
   151→  return makeRequest(url, { ...options, headers });
   152→}
   153→
   154→// ==================== Request Builders ====================
   155→
   156→/**
   157→ * Builds a request body for OpenAI chat completions
   158→ */
   159→export function buildOpenAIChatRequest(messages: Array<{role: string; content: string}>, model?: string): string {
   160→  return JSON.stringify({
   161→    model: model || 'glm-4',
   162→    messages,
   163→    stream: false,
   164→  });
   165→}
   166→
   167→/**
   168→ * Builds a streaming request body for OpenAI chat completions
   169→ */
   170→export function buildOpenAIStreamingRequest(messages: Array<{role: string; content: string}>, model?: string): string {
   171→  return JSON.stringify({
   172→    model: model || 'glm-4',
   173→    messages,
   174→    stream: true,
   175→  });
   176→}
   177→
   178→/**
   179→ * Builds a request body for Anthropic messages
   180→ */
