   191→/**
   192→ * Test environment setup helper for Vitest
   193→ *
   194→ * Usage in beforeEach:
   195→ * ```ts
   196→ * let testEnv: TestEnvironment;
   197→ * beforeEach(() => {
   198→ *   testEnv = setupTestEnvironment();
   199→ * });
   200→ *
   201→ * afterEach(() => {
   202→ *   teardownTestEnvironment(testEnv);
   203→ * });
   204→ * ```
   205→ */
   206→export function createTestSetup() {
   207→  let env: TestEnvironment | null = null;
   208→
   209→  return {
   210→    setup: () => {
   211→      if (env) {
   212→        throw new Error('Test environment already set up');
   213→      }
   214→      env = setupTestEnvironment();
   215→      return env;
   216→    },
   217→    teardown: () => {
   218→      if (!env) {
   219→        throw new Error('Test environment not set up');
   220→      }
   221→      teardownTestEnvironment(env);
   222→      env = null;
   223→    },
   224→    getEnv: () => {
   225→      if (!env) {
   226→        throw new Error('Test environment not set up');
   227→      }
   228→      return env;
   229→    },
   230→  };
   231→}
