     1→/**
     2→ * Integration Test Setup
     3→ *
     4→ * Provides utilities for setting up and tearing down the test environment,
     5→ * including temporary data directories, environment variables, and cleanup.
     6→ */
     7→
     8→import fs from 'fs';
     9→import path from 'path';
    10→import type { ApiKey } from '../../src/types';
    11→import { TEST_API_KEYS } from './fixtures';
    12→
    13→/**
    14→ * Test environment configuration
    15→ */
    16→export interface TestEnvironment {
    17→  testDataDir: string;
    18→  testDataFile: string;
    19→  originalEnv: NodeJS.ProcessEnv;
    20→}
    21→
    22→/**
    23→ * Sets up the test environment before running tests
    24→ *
    25→ * Creates a temporary data directory, sets environment variables,
    26→ * and initializes test API keys.
    27→ *
    28→ * @param customDir - Optional custom directory path
    29→ * @returns Test environment configuration
    30→ */
    31→export function setupTestEnvironment(customDir?: string): TestEnvironment {
    32→  // Store original environment variables
    33→  const originalEnv = { ...process.env };
    34→
    35→  // Create temporary test data directory
    36→  const testDataDir = customDir || path.join(process.cwd(), 'data', 'test');
    37→  const testDataFile = path.join(testDataDir, 'apikeys.json');
    38→
    39→  // Create directory if it doesn't exist
    40→  if (!fs.existsSync(testDataDir)) {
    41→    fs.mkdirSync(testDataDir, { recursive: true });
    42→  }
    43→
    44→  // Set environment variables for testing
    45→  process.env.DATA_FILE = testDataFile;
    46→  process.env.ZAI_API_KEY = 'test_zai_api_key';
    47→  process.env.DEFAULT_MODEL = 'glm-4';
    48→  process.env.NODE_ENV = 'test';
    49→  process.env.PORT = '0'; // Use random port
    50→
    51→  // Initialize test API keys file
    52→  writeTestApiKeys(testDataFile, TEST_API_KEYS);
    53→
    54→  return {
    55→    testDataDir,
    56→    testDataFile,
    57→    originalEnv,
    58→  };
    59→}
    60→
    61→/**
    62→ * Tears down the test environment after running tests
    63→ *
    64→ * Cleans up temporary files and restores environment variables.
    65→ *
    66→ * @param env - Test environment configuration
    67→ * @param cleanupData - Whether to remove test data files (default: true)
    68→ */
    69→export function teardownTestEnvironment(env: TestEnvironment, cleanupData = true): void {
    70→  const { testDataDir, testDataFile, originalEnv } = env;
    71→
    72→  // Clean up test data files if requested
    73→  if (cleanupData) {
    74→    cleanupTestDataFiles(testDataFile);
    75→  }
    76→
    77→  // Clean up test data directory if empty
    78→  try {
    79→    const files = fs.readdirSync(testDataDir);
    80→    if (files.length === 0) {
    81→      fs.rmdirSync(testDataDir);
    82→    }
    83→  } catch (e) {
    84→    // Directory might not exist or have permission issues
    85→    // Ignore cleanup errors
    86→  }
    87→
    88→  // Restore original environment variables
    89→  process.env = originalEnv;
    90→}
    91→
    92→/**
    93→ * Writes test API keys to a file
    94→ */
    95→export function writeTestApiKeys(filePath: string, keys: ApiKey[]): void {
    96→  const dir = path.dirname(filePath);
    97→  if (!fs.existsSync(dir)) {
    98→    fs.mkdirSync(dir, { recursive: true });
    99→  }
   100→
   101→  fs.writeFileSync(
   102→    filePath,
   103→    JSON.stringify({ keys }, null, 2),
   104→    'utf-8'
   105→  );
   106→}
   107→
   108→/**
   109→ * Cleans up test data files
   110→ */
   111→export function cleanupTestDataFiles(dataFile: string): void {
   112→  // Remove main data file
   113→  if (fs.existsSync(dataFile)) {
   114→    fs.unlinkSync(dataFile);
   115→  }
   116→
   117→  // Remove lock file if it exists
   118→  const lockFile = dataFile + '.lock';
   119→  if (fs.existsSync(lockFile)) {
   120→    try {
   121→      fs.rmdirSync(lockFile);
   122→    } catch (e) {
   123→      // Lock directory might not be empty or accessible
   124→      // Ignore cleanup errors
   125→    }
   126→  }
   127→
   128→  // Remove temp file if it exists
   129→  const tempFile = dataFile + '.tmp';
   130→  if (fs.existsSync(tempFile)) {
   131→    fs.unlinkSync(tempFile);
   132→  }
   133→}
   134→
   135→/**
   136→ * Sets up a test environment with custom API keys
   137→ *
   138→ * @param keys - Custom API keys to use for testing
   139→ * @param customDir - Optional custom directory path
   140→ * @returns Test environment configuration
   141→ */
   142→export function setupTestEnvironmentWithKeys(
   143→  keys: ApiKey[],
   144→  customDir?: string
   145→): TestEnvironment {
   146→  const env = setupTestEnvironment(customDir);
   147→  writeTestApiKeys(env.testDataFile, keys);
   148→  return env;
   149→}
   150→
   151→/**
   152→ * Resets test API keys to initial state
   153→ *
   154→ * Useful for tests that modify API key data and need to reset.
   155→ *
   156→ * @param env - Test environment configuration
   157→ */
   158→export function resetTestApiKeys(env: TestEnvironment): void {
   159→  writeTestApiKeys(env.testDataFile, TEST_API_KEYS);
   160→}
   161→
   162→/**
   163→ * Creates a backup of the current API keys file
   164→ *
   165→ * Useful for tests that need to restore state after modifications.
   166→ *
   167→ * @param env - Test environment configuration
   168→ * @returns Backup file path
   169→ */
   170→export function backupApiKeys(env: TestEnvironment): string {
   171→  const backupPath = env.testDataFile + '.backup';
   172→  if (fs.existsSync(env.testDataFile)) {
   173→    fs.copyFileSync(env.testDataFile, backupPath);
   174→  }
   175→  return backupPath;
   176→}
   177→
   178→/**
   179→ * Restores API keys from a backup
   180→ *
   181→ * @param env - Test environment configuration
   182→ * @param backupPath - Backup file path
   183→ */
   184→export function restoreApiKeys(env: TestEnvironment, backupPath: string): void {
   185→  if (fs.existsSync(backupPath)) {
   186→    fs.copyFileSync(backupPath, env.testDataFile);
   187→    fs.unlinkSync(backupPath);
   188→  }
   189→}
   190→
   191→/**
   192→ * Test environment setup helper for Vitest
   193→ *
   194→ * Usage in beforeEach:
   195→ * ```ts
   196→ * let testEnv: TestEnvironment;
   197→ * beforeEach(() => {
   198→ *   testEnv = setupTestEnvironment();
   199→ * });
   200→ *
   201→ * afterEach(() => {
   202→ *   teardownTestEnvironment(testEnv);
   203→ * });
   204→ * ```
   205→ */
   206→export function createTestSetup() {
   207→  let env: TestEnvironment | null = null;
   208→
   209→  return {
   210→    setup: () => {
   211→      if (env) {
   212→        throw new Error('Test environment already set up');
   213→      }
   214→      env = setupTestEnvironment();
   215→      return env;
   216→    },
   217→    teardown: () => {
   218→      if (!env) {
   219→        throw new Error('Test environment not set up');
   220→      }
   221→      teardownTestEnvironment(env);
   222→      env = null;
   223→    },
   224→    getEnv: () => {
   225→      if (!env) {
   226→        throw new Error('Test environment not set up');
   227→      }
   228→      return env;
   229→    },
   230→  };
   231→}
   232→
   233→/**
   234→ * Sets up environment for a specific test scenario
   235→ *
   236→ * @param scenario - Scenario configuration
   237→ * @returns Test environment configuration
   238→ */
   239→export interface TestScenario {
   240→  keys?: ApiKey[];
   241→  envVars?: Record<string, string>;
   242→  customDir?: string;
   243→}
   244→
   245→export function setupTestScenario(scenario: TestScenario = {}): TestEnvironment {
   246→  // Set up base environment
   247→  const env = scenario.keys
   248→    ? setupTestEnvironmentWithKeys(scenario.keys, scenario.customDir)
   249→    : setupTestEnvironment(scenario.customDir);
   250→
   251→  // Set custom environment variables
   252→  if (scenario.envVars) {
   253→    for (const [key, value] of Object.entries(scenario.envVars)) {
   254→      process.env[key] = value;
   255→    }
   256→  }
   257→
   258→  return env;
   259→}
   260→
   261→/**
   262→ * Gets the current test data file path from environment
   263→ */
   264→export function getTestDataFilePath(): string {
   265→  return process.env.DATA_FILE || path.join(process.cwd(), 'data', 'apikeys.json');
   266→}
   267→
   268→/**
   269→ * Checks if running in test environment
   270→ */
   271→export function isTestEnvironment(): boolean {
   272→  return process.env.NODE_ENV === 'test';
   273→}
   274→
   275→/**
   276→ * Validates test environment setup
   277→ *
   278→ * Throws an error if the test environment is not properly configured.
   279→ */
   280→export function validateTestEnvironment(): void {
   281→  if (!process.env.DATA_FILE) {
   282→    throw new Error('DATA_FILE environment variable not set');
   283→  }
   284→
   285→  if (!process.env.ZAI_API_KEY) {
   286→    throw new Error('ZAI_API_KEY environment variable not set');
   287→  }
   288→
   289→  const dataFile = getTestDataFilePath();
   290→  const dataDir = path.dirname(dataFile);
   291→
   292→  if (!fs.existsSync(dataDir)) {
   293→    throw new Error(`Test data directory does not exist: ${dataDir}`);
   294→  }
   295→}
