     1→import fs from 'fs';
     2→import path from 'path';
     3→import type { ApiKeysData, ApiKey } from './types.js';
     4→
     5→const DATA_FILE = process.env.DATA_FILE || path.join(process.cwd(), 'data/apikeys.json');
     6→const LOCK_FILE = DATA_FILE + '.lock';
     7→
     8→// Ensure data directory exists
     9→const DATA_DIR = path.dirname(DATA_FILE);
    10→if (!fs.existsSync(DATA_DIR)) {
    11→  fs.mkdirSync(DATA_DIR, { recursive: true });
    12→}
    13→
    14→// Simple file lock using mkdir (atomic on Unix)
    15→export async function withLock<T>(fn: () => Promise<T>): Promise<T> {
    16→  const maxRetries = 10;
    17→  const retryDelay = 50;
    18→
    19→  for (let i = 0; i < maxRetries; i++) {
    20→    try {
    21→      fs.mkdirSync(LOCK_FILE, { mode: 0o755 });
    22→      break;
    23→    } catch (e: unknown) {
    24→      if ((e as NodeJS.ErrnoException).code !== 'EEXIST' || i === maxRetries - 1) throw e;
    25→      await new Promise(r => setTimeout(r, retryDelay));
    26→    }
    27→  }
    28→
    29→  try {
    30→    return await fn();
    31→  } finally {
    32→    fs.rmdirSync(LOCK_FILE);
    33→  }
    34→}
    35→
    36→export async function readApiKeys(): Promise<ApiKeysData> {
    37→  try {
    38→    const content = await fs.promises.readFile(DATA_FILE, 'utf-8');
    39→    return JSON.parse(content);
    40→  } catch {
    41→    return { keys: [] };
    42→  }
    43→}
    44→
    45→export async function writeApiKeys(data: ApiKeysData): Promise<void> {
    46→  const tempFile = DATA_FILE + '.tmp';
    47→  await fs.promises.writeFile(tempFile, JSON.stringify(data, null, 2), 'utf-8');
    48→  await fs.promises.rename(tempFile, DATA_FILE);
    49→}
    50→
    51→export async function findApiKey(key: string): Promise<ApiKey | null> {
    52→  return await withLock(async () => {
    53→    const data = await readApiKeys();
    54→    return data.keys.find(k => k.key === key) || null;
    55→  });
    56→}
    57→
    58→export async function updateApiKeyUsage(
    59→  key: string,
    60→  tokensUsed: number,
    61→  _model: string
    62→): Promise<void> {
    63→  await withLock(async () => {
    64→    const data = await readApiKeys();
    65→    const keyIndex = data.keys.findIndex(k => k.key === key);
    66→
    67→    if (keyIndex === -1) return;
    68→
    69→    const apiKey = data.keys[keyIndex];
    70→    const now = new Date().toISOString();
    71→
    72→    // Update last_used and total tokens
    73→    apiKey.last_used = now;
    74→    apiKey.total_lifetime_tokens += tokensUsed;
    75→
    76→    // Find or create current window
    77→    const fiveHoursAgo = new Date(Date.now() - 5 * 60 * 60 * 1000).toISOString();
    78→    let currentWindow = apiKey.usage_windows.find(
    79→      w => w.window_start >= fiveHoursAgo
    80→    );
    81→
    82→    if (!currentWindow) {
    83→      currentWindow = { window_start: now, tokens_used: 0 };
    84→      apiKey.usage_windows.push(currentWindow);
    85→    }
    86→
    87→    currentWindow.tokens_used += tokensUsed;
    88→
    89→    // Clean up old windows
    90→    apiKey.usage_windows = apiKey.usage_windows.filter(
    91→      w => w.window_start >= fiveHoursAgo
    92→    );
    93→
    94→    await writeApiKeys(data);
    95→  });
    96→}
    97→
    98→export async function getKeyStats(key: string): Promise<ApiKey | null> {
    99→  return await findApiKey(key);
   100→}
