    11→
    12→export async function validateApiKey(
    13→  keyHeader: string | undefined
    14→): Promise<ValidationResult> {
    15→  if (!keyHeader) {
    16→    return {
    17→      valid: false,
    18→      error: 'API key required. Use Authorization: Bearer <key> or x-api-key: <key>',
    19→      statusCode: 401,
    20→    };
    21→  }
    22→
    23→  const key = keyHeader.replace(/^Bearer\s+/i, '').trim();
    24→
    25→  if (!key) {
    26→    return {
    27→      valid: false,
    28→      error: 'API key cannot be empty',
    29→      statusCode: 401,
    30→    };
    31→  }
    32→
    33→  const apiKey = await findApiKey(key);
    34→
    35→  if (!apiKey) {
    36→    return {
    37→      valid: false,
    38→      error: 'Invalid API key',
    39→      statusCode: 401,
    40→    };
    41→  }
    42→
    43→  if (isKeyExpired(apiKey)) {
    44→    return {
    45→      valid: false,
    46→      error: `API key expired on ${apiKey.expiry_date}`,
    47→      statusCode: 403,
    48→    };
    49→  }
    50→
    51→  return {
    52→    valid: true,
    53→    apiKey,
    54→  };
    55→}
