     1→import type { ApiKey } from './types.js';
     2→import { getModelForKey } from './validator.js';
     3→import { updateApiKeyUsage } from './storage.js';
     4→
     5→const ZAI_API_BASE = 'https://api.z.ai/api/coding/paas/v4';
     6→const ZAI_API_KEY = process.env.ZAI_API_KEY;
     7→
     8→export interface ProxyOptions {
     9→  apiKey: ApiKey;
    10→  path: string;
    11→  method: string;
    12→  headers: Record<string, string>;
    13→  body: string | null;
    14→}
    15→
    16→export interface ProxyResult {
    17→  success: boolean;
    18→  status: number;
    19→  headers: Record<string, string>;
    20→  body: string;
    21→  tokensUsed?: number;
    22→}
    23→
    24→export async function proxyRequest(options: ProxyOptions): Promise<ProxyResult> {
    25→  const { apiKey, path, method, headers, body } = options;
    26→
    27→  // Runtime check for ZAI_API_KEY
    28→  if (!ZAI_API_KEY) {
    29→    return {
    30→      success: false,
    31→      status: 500,
    32→      headers: { 'content-type': 'application/json' },
    33→      body: JSON.stringify({
    34→        error: {
    35→          message: 'ZAI_API_KEY environment variable is not configured',
    36→          type: 'configuration_error',
    37→        },
    38→      }),
    39→      tokensUsed: 0,
    40→    };
    41→  }
    42→
    43→  const model = getModelForKey(apiKey);
    44→
    45→  // Build target URL
    46→  // Z.AI uses /v4 base, OpenAI compatibility but without /v1 prefix
    47→  // e.g., /v1/chat/completions -> /chat/completions -> /v4/chat/completions
    48→  const cleanPath = path.startsWith('/v1/') ? path.substring(4) : path;
    49→  const slash = cleanPath.startsWith('/') ? '' : '/';
    50→  const targetUrl = `${ZAI_API_BASE}${slash}${cleanPath}`;
    51→
    52→  // Prepare headers for Z.AI - always forward Authorization with master key
    53→  const proxyHeaders: Record<string, string> = {
    54→    'Authorization': `Bearer ${ZAI_API_KEY}`,
    55→  };
    56→
    57→  // Forward relevant headers from client (but not Authorization)
    58→  const forwardHeaders = ['content-type', 'accept', 'user-agent'];
    59→  for (const h of forwardHeaders) {
    60→    const key = Object.keys(headers).find(k => k.toLowerCase() === h);
    61→    if (key) {
    62→      proxyHeaders[key] = headers[key];
    63→    }
    64→  }
    65→
    66→  // Inject/override model in request body
    67→  let processedBody = body;
    68→  let tokensUsed = 0;
    69→
    70→  if (body && (method === 'POST' || method === 'PUT' || method === 'PATCH')) {
    71→    try {
    72→      const bodyJson = JSON.parse(body);
    73→
    74→      // Inject model for chat/completions endpoint
    75→      if (path.includes('/chat/completions') || path.includes('/completions')) {
    76→        bodyJson.model = model;
    77→      }
    78→
    79→      processedBody = JSON.stringify(bodyJson);
    80→    } catch {
    81→      // Body not JSON, leave as-is
    82→    }
    83→  }
    84→
    85→  // Make request to Z.AI
    86→  try {
    87→    const response = await fetch(targetUrl, {
    88→      method,
    89→      headers: proxyHeaders,
    90→      body: processedBody,
    91→    });
    92→
    93→    // Get response body
    94→    const responseBody = await response.text();
    95→
    96→    // Extract token usage from response
    97→    if (response.ok) {
    98→      try {
    99→        const responseJson = JSON.parse(responseBody);
   100→
   101→        // OpenAI format usage
   102→        if (responseJson.usage) {
   103→          tokensUsed = responseJson.usage.total_tokens || 0;
   104→        }
   105→
   106→        // Update usage after successful request
   107→        if (tokensUsed > 0) {
   108→          // Don't await - fire and forget for performance
   109→          updateApiKeyUsage(apiKey.key, tokensUsed, model).catch(console.error);
   110→        }
   111→      } catch {
   112→        // Response not JSON or no usage field
   113→      }
   114→    }
   115→
   116→    // Build response headers
   117→    const responseHeaders: Record<string, string> = {
   118→      'content-type': response.headers.get('content-type') || 'application/json',
   119→    };
   120→
   121→    return {
   122→      success: response.ok,
   123→      status: response.status,
   124→      headers: responseHeaders,
   125→      body: responseBody,
   126→      tokensUsed,
   127→    };
   128→  } catch (error: any) {
   129→    return {
   130→      success: false,
   131→      status: 502,
   132→      headers: { 'content-type': 'application/json' },
   133→      body: JSON.stringify({
   134→        error: {
   135→          message: `Upstream request failed: ${error.message}`,
   136→          type: 'upstream_error',
   137→        },
   138→      }),
   139→      tokensUsed: 0,
   140→    };
   141→  }
   142→}
