     1→/**
     2→ * Integration Test Helpers
     3→ *
     4→ * Provides utilities for setting up test servers, making HTTP requests,
     5→ * and validating responses in integration tests.
     6→ */
     7→
     8→import { serve } from 'bun';
     9→import fs from 'fs';
    10→import path from 'path';
    11→import type { ApiKey, UsageWindow } from '../../src/types';
    12→
    13→/**
    14→ * Test server interface
    15→ */
    16→export interface TestServer {
    17→  url: string;
    18→  port: number;
    19→  stop: () => Promise<void>;
    20→}
    21→
    22→/**
    23→ * HTTP response wrapper
    24→ */
    25→export interface TestResponse {
    26→  status: number;
    27→  headers: Headers;
    28→  body: string;
    29→  json: () => any;
    30→}
    31→
    32→/**
    33→ * Rate limit info for validation
    34→ */
    35→export interface RateLimitInfo {
    36→  allowed: boolean;
    37→  tokensUsed: number;
    38→  tokensLimit: number;
    39→  windowStart?: string;
    40→  windowEnd?: string;
    41→  reason?: string;
    42→  retryAfter?: number;
    43→}
    44→
    45→/**
    46→ * Starts a test server instance
    47→ *
    48→ * @param port - Optional port number (defaults to random available port)
    49→ * @returns Test server instance with URL and stop function
    50→ */
    51→export async function startTestServer(port?: number): Promise<TestServer> {
    52→  // Find available port if not specified
    53→  const actualPort = port || (await findAvailablePort());
    54→
    55→  // Import app dynamically to ensure test environment is set up first
    56→  const app = (await import('../../src/index')).default;
    57→
    58→  // Start the server
    59→  const server = serve({
    60→    fetch: app.fetch,
    61→    port: actualPort,
    62→  });
    63→
    64→  return {
    65→    url: `http://localhost:${actualPort}`,
    66→    port: actualPort,
    67→    stop: async () => {
    68→      server.stop();
    69→    },
    70→  };
    71→}
    72→
    73→/**
    74→ * Finds an available port for testing
    75→ */
    76→async function findAvailablePort(): Promise<number> {
    77→  // Try ports starting from 3001
    78→  for (let port = 3001; port < 4000; port++) {
    79→    try {
    80→      const server = serve({ port, fetch: () => new Response('ok') });
    81→      server.stop();
    82→      return port;
    83→    } catch (e) {
    84→      // Port in use, try next
    85→      continue;
    86→    }
    87→  }
    88→  throw new Error('No available port found for test server');
    89→}
    90→
    91→/**
    92→ * Makes an HTTP request to the test server
    93→ *
    94→ * @param url - Request URL
    95→ * @param options - Request options
    96→ * @returns Test response wrapper
    97→ */
    98→export async function makeRequest(
    99→  url: string,
   100→  options: RequestInit = {}
   101→): Promise<TestResponse> {
   102→  const response = await fetch(url, options);
   103→
   104→  const body = await response.text();
   105→
   106→  return {
   107→    status: response.status,
   108→    headers: response.headers,
   109→    body,
   110→    json: () => JSON.parse(body),
   111→  };
   112→}
   113→
   114→/**
   115→ * Makes an authenticated request with API key
   116→ *
   117→ * @param url - Request URL
   118→ * @param apiKey - API key for authentication
   119→ * @param options - Request options
   120→ * @returns Test response wrapper
   121→ */
   122→export async function makeAuthenticatedRequest(
   123→  url: string,
   124→  apiKey: string,
   125→  options: RequestInit = {}
   126→): Promise<TestResponse> {
   127→  const headers = {
   128→    ...options.headers,
   129→    Authorization: `Bearer ${apiKey}`,
   130→  };
   131→
   132→  return makeRequest(url, { ...options, headers });
   133→}
   134→
   135→/**
   136→ * Makes an authenticated request with x-api-key header
   137→ *
   138→ * @param url - Request URL
   139→ * @param apiKey - API key for authentication
   140→ * @param options - Request options
   141→ * @returns Test response wrapper
   142→ */
   143→export async function makeRequestWithXApiKey(
   144→  url: string,
   145→  apiKey: string,
   146→  options: RequestInit = {}
   147→): Promise<TestResponse> {
   148→  const headers = {
   149→    ...options.headers,
   150→    'x-api-key': apiKey,
   151→  };
   152→
   153→  return makeRequest(url, { ...options, headers });
   154→}
   155→
   156→// ==================== Request Builders ====================
   157→
   158→/**
   159→ * Builds a request body for OpenAI chat completions
   160→ */
   161→export function buildOpenAIChatRequest(messages: Array<{role: string; content: string}>, model?: string): string {
   162→  return JSON.stringify({
   163→    model: model || 'glm-4',
   164→    messages,
   165→    stream: false,
   166→  });
   167→}
   168→
   169→/**
   170→ * Builds a streaming request body for OpenAI chat completions
   171→ */
   172→export function buildOpenAIStreamingRequest(messages: Array<{role: string; content: string}>, model?: string): string {
   173→  return JSON.stringify({
   174→    model: model || 'glm-4',
   175→    messages,
   176→    stream: true,
   177→  });
   178→}
   179→
   180→/**
   181→ * Builds a request body for Anthropic messages
   182→ */
   183→export function buildAnthropicMessagesRequest(messages: Array<{role: string; content: string}>, model?: string): string {
   184→  return JSON.stringify({
