   203→
   204→// ==================== Response Validators ====================
   205→
   206→/**
   207→ * Validates that a response has the expected status code
   208→ */
   209→export function expectStatus(response: TestResponse, expectedStatus: number): void {
   210→  if (response.status !== expectedStatus) {
   211→    throw new Error(
   212→      `Expected status ${expectedStatus}, got ${response.status}\nBody: ${response.body}`
   213→    );
   214→  }
   215→}
   216→
   217→/**
   218→ * Validates that a response contains expected JSON properties
   219→ */
   220→export function expectJsonProperties(response: TestResponse, properties: string[]): void {
   221→  const json = response.json();
   222→  for (const prop of properties) {
   223→    if (!(prop in json)) {
   224→      throw new Error(`Expected property "${prop}" not found in response JSON`);
   225→    }
   226→  }
   227→}
   228→
   229→/**
   230→ * Validates health endpoint response format
   231→ */
   232→export function validateHealthResponse(response: TestResponse): void {
   233→  expectStatus(response, 200);
