   239→/**
   240→ * Validates stats endpoint response format
   241→ */
   242→export function validateStatsResponse(response: TestResponse, expectedKey: string): void {
   243→  expectStatus(response, 200);
   244→  const json = response.json();
   245→  expectJsonProperties(response, ['key', 'name', 'model', 'token_limit_per_5h']);
   246→  expect(json.key).toBe(expectedKey);
   247→}
   248→
   249→/**
   250→ * Validates error response format
   251→ */
   252→export function validateErrorResponse(
