     1→import type { ApiKey } from './types.js';
     2→import { getModelForKey } from './validator.js';
     3→import { updateApiKeyUsage } from './storage.js';
     4→
     5→const ZAI_ANTHROPIC_BASE = 'https://open.bigmodel.cn/api/anthropic';
     6→
     7→export interface AnthropicProxyOptions {
     8→  apiKey: ApiKey;
     9→  path: string;
    10→  method: string;
    11→  headers: Record<string, string>;
    12→  body: string | null;
    13→}
    14→
    15→export interface AnthropicProxyResult {
    16→  success: boolean;
    17→  status: number;
    18→  headers: Record<string, string>;
    19→  body: string;
    20→  tokensUsed?: number;
    21→}
    22→
    23→export async function proxyAnthropicRequest(options: AnthropicProxyOptions): Promise<AnthropicProxyResult> {
    24→  const { apiKey, path, method, headers, body } = options;
    25→
    26→  // Runtime check for ZAI_API_KEY
    27→  if (!process.env.ZAI_API_KEY) {
    28→    return {
    29→      success: false,
    30→      status: 500,
    31→      headers: { 'content-type': 'application/json' },
    32→      body: JSON.stringify({
    33→        error: {
    34→          message: 'ZAI_API_KEY environment variable is not configured',
    35→          type: 'configuration_error',
    36→        },
    37→      }),
    38→      tokensUsed: 0,
    39→    };
    40→  }
    41→
    42→  const model = getModelForKey(apiKey);
    43→
    44→  // Build target URL for Anthropic API
    45→  // Path will be /v1/messages, so we use it directly
    46→  const targetUrl = `${ZAI_ANTHROPIC_BASE}${path}`;
    47→
    48→  // Prepare headers for Z.AI Anthropic API
    49→  const proxyHeaders: Record<string, string> = {
    50→    'x-api-key': process.env.ZAI_API_KEY,
    51→    'anthropic-version': headers['anthropic-version'] || '2023-06-01',
    52→  };
    53→
    54→  // Forward relevant headers from client (but not x-api-key)
    55→  const forwardHeaders = ['content-type', 'accept', 'user-agent'];
    56→  for (const h of forwardHeaders) {
    57→    const key = Object.keys(headers).find(k => k.toLowerCase() === h);
    58→    if (key) {
    59→      proxyHeaders[key] = headers[key];
    60→    }
    61→  }
    62→
    63→  // Inject/override model in request body
    64→  let processedBody = body;
    65→  let tokensUsed = 0;
    66→
    67→  if (body && (method === 'POST' || method === 'PUT' || method === 'PATCH')) {
    68→    try {
    69→      const bodyJson = JSON.parse(body);
    70→
    71→      // Inject model for messages endpoint
    72→      if (path.includes('/messages')) {
    73→        bodyJson.model = model;
    74→      }
    75→
    76→      processedBody = JSON.stringify(bodyJson);
    77→    } catch {
    78→      // Body not JSON, leave as-is
    79→    }
    80→  }
    81→
    82→  // Make request to Z.AI
    83→  try {
    84→    const response = await fetch(targetUrl, {
    85→      method,
    86→      headers: proxyHeaders,
    87→      body: processedBody,
    88→    });
    89→
    90→    // Get response body
    91→    const responseBody = await response.text();
    92→
    93→    // Extract token usage from response
    94→    if (response.ok) {
    95→      try {
    96→        const responseJson = JSON.parse(responseBody);
    97→
    98→        // Anthropic format usage
    99→        if (responseJson.usage) {
   100→          tokensUsed = responseJson.usage.input_tokens + responseJson.usage.output_tokens;
   101→        }
   102→
   103→        // Update usage after successful request
   104→        if (tokensUsed > 0) {
   105→          // Don't await - fire and forget for performance
   106→          updateApiKeyUsage(apiKey.key, tokensUsed, model).catch(console.error);
   107→        }
   108→      } catch {
   109→        // Response not JSON or no usage field
   110→      }
   111→    }
   112→
   113→    // Build response headers
   114→    const responseHeaders: Record<string, string> = {
   115→      'content-type': response.headers.get('content-type') || 'application/json',
   116→    };
   117→
   118→    // Handle streaming response
   119→    if (response.headers.get('content-type')?.includes('text/event-stream')) {
   120→      responseHeaders['content-type'] = 'text/event-stream';
   121→    }
   122→
   123→    return {
   124→      success: response.ok,
   125→      status: response.status,
   126→      headers: responseHeaders,
   127→      body: responseBody,
   128→      tokensUsed,
   129→    };
   130→  } catch (error: unknown) {
   131→    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   132→    return {
   133→      success: false,
   134→      status: 502,
   135→      headers: { 'content-type': 'application/json' },
   136→      body: JSON.stringify({
   137→        error: {
   138→          message: `Upstream request failed: ${errorMessage}`,
   139→          type: 'upstream_error',
   140→        },
   141→      }),
   142→      tokensUsed: 0,
   143→    };
   144→  }
   145→}
